-- ----------------------------
-- Table structure for market_signal
-- ----------------------------
DROP TABLE IF EXISTS `market_signal`;
CREATE TABLE `market_signal` (
  `signal_id` bigint NOT NULL AUTO_INCREMENT COMMENT '信号ID',
  `symbol` varchar(20) NOT NULL COMMENT '交易对',
  `interval` varchar(10) NOT NULL COMMENT 'K线周期',
  `current_price` decimal(20,8) DEFAULT NULL COMMENT '当前价格',
  `support1` decimal(20,8) DEFAULT NULL COMMENT '支撑位1',
  `support2` decimal(20,8) DEFAULT NULL COMMENT '支撑位2',
  `support3` decimal(20,8) DEFAULT NULL COMMENT '支撑位3',
  `resistance1` decimal(20,8) DEFAULT NULL COMMENT '压力位1',
  `resistance2` decimal(20,8) DEFAULT NULL COMMENT '压力位2',
  `resistance3` decimal(20,8) DEFAULT NULL COMMENT '压力位3',
  `near_breakout` char(1) DEFAULT 'N' COMMENT '是否接近突破(Y/N)',
  `breakout_direction` varchar(10) DEFAULT NULL COMMENT '突破方向(UP/DOWN)',
  `is_overbought` char(1) DEFAULT 'N' COMMENT '是否超买(Y/N)',
  `is_oversold` char(1) DEFAULT 'N' COMMENT '是否超卖(Y/N)',
  `rsi_value` decimal(5,2) DEFAULT NULL COMMENT 'RSI值',
  `macd_signal` varchar(20) DEFAULT NULL COMMENT 'MACD信号',
  `bollinger_position` varchar(20) DEFAULT NULL COMMENT '布林带位置',
  `volume_status` varchar(20) DEFAULT NULL COMMENT '成交量状态',
  `trend_direction` varchar(20) DEFAULT NULL COMMENT '趋势方向(UP/DOWN/SIDEWAYS)',
  `trend_strength` int DEFAULT NULL COMMENT '趋势强度(0-100)',
  `signal_score` int DEFAULT NULL COMMENT '综合信号评分(0-100)',
  `recommend_action` varchar(10) DEFAULT NULL COMMENT '推荐操作(BUY/SELL/HOLD)',
  `recommend_reason` varchar(500) DEFAULT NULL COMMENT '推荐原因',
  `risk_level` varchar(10) DEFAULT NULL COMMENT '风险等级(LOW/MEDIUM/HIGH)',
  `stop_loss_price` decimal(20,8) DEFAULT NULL COMMENT '止损建议价格',
  `take_profit_price` decimal(20,8) DEFAULT NULL COMMENT '止盈建议价格',
  `signal_reliability` int DEFAULT NULL COMMENT '信号可信度(0-100)',
  `false_signal_risk` varchar(10) DEFAULT NULL COMMENT '假信号风险(LOW/MEDIUM/HIGH)',
  `analysis_time` datetime DEFAULT NULL COMMENT '分析时间',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`signal_id`),
  KEY `idx_symbol` (`symbol`),
  KEY `idx_interval` (`interval`),
  KEY `idx_analysis_time` (`analysis_time`)
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='市场信号分析表';

